/** Animate RGB Functions */
function animateBg(i) {document.body.style.backgroundColor = 'hsl(' + i + ', 255%, 40%)'; setTimeout(function() {animateBg(++i)}, i); // Adjust LED Color Change Speed here}
}


function SetBG_RGB(i) {Warn("The background Got modified by a current running function: SetBG_RGB() (THIS CAN ONLY BE MODIFIED ONCE)")
  animateBg(i); // Frame Input
}

