function AnimateElement_RGB(i,e){
  GetFromID(i).style.color = 'hsl(' + e + ', 255%, 40%)';
  setTimeout(function(){
    AnimateElement_RGB(e++)
  }, e)
}

function SetElement_RGB(i,e){
  Warn("A element Got Modiied by a current running function: AnimateElement_RGB")
  AnimateElement_RGB(i,e)
}

